import { useParams } from "react-router-dom";
import { useEffect, useState } from "react";

const CountryInfo = () => {
    const [info, setInfo] = useState([]);
 const { id } = useParams();

 useEffect(() => {
    fetch(`https://restcountries.com/v2/${id}`)
      .then((res) => res.json())
      .then((data) => {
        setInfo(data);
        // setIsLoading(false);
        log
      })
      .catch((err) => console.log(err));
  });

    return (
    <div className=" p-6 py-8 md:px-14 md:p-12">
      <button className="text-black border inline-flex items-center py-2 px-4 rounded border-slate-200 shadow-md">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          viewBox="0 0 24 24"
          width="15"
          height="15"
        >
          <path fill="none" d="M0 0h24v24H0z" />
          <path d="M7.828 11H20v2H7.828l5.364 5.364-1.414 1.414L4 12l7.778-7.778 1.414 1.414z" />
        </svg> 
        <div className="px-3">Back</div>
      </button>

      <div className="container relative flex flex-row flex-wrap py-6 ">
        <div className="md:flex md:basis-1/2">        
        <a href="#!" data-mdb-ripple="true" data-mdb-ripple-color="light">
          <img className="w-full h-60" src="" alt="country-imag" />
        </a></div>
        <div className="container md:basis-1/2 py-6">
          <h1 className="text-md font-bold pb-6">Belgium</h1>

          <div className="flex md:flex md:flex-row ">
            <div className="basis-1/2">
                <div className="pb-2"><b>Native Name:</b>{}</div>
                <p className="pb-2"><b>Population:</b> <span>Euro</span></p>
                <p className="pb-2"><b>Region:</b> <span>Euro</span></p>
                <p className="pb-2"><b>Sub Region:</b> <span>Euro</span></p>
                <p><b>Capital:</b> <span>Euro</span></p>
            </div>
            <div className="basis-1/2">
                <p className="pb-2"><b>Top Level Domain:</b> <span>be</span></p>
                <p className="pb-2"><b>Currencies:</b> <span>Euro</span></p>
                <p className="pb-2"><b>Laguages:</b> <span>be</span></p>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default CountryInfo;
